private var cacheAccessTimes: [String: Date] = [:]

private func cleanupCache() {
    // Remove least recently used items to maintain cache size
    let sortedByAccessTime = cacheAccessTimes.sorted { $0.value < $1.value }
    let keysToRemove = Array(sortedByAccessTime.prefix(ocrCache.count - maxCacheSize).map { $0.key })

    for key in keysToRemove {
        ocrCache.removeValue(forKey: key)
        cacheAccessTimes.removeValue(forKey: key)
    }

    logger.debug("Cleaned up OCR cache, removed \(keysToRemove.count) items")
}

